mapper.select:
SELECT SUM(success_num) successNum,SUM(send_num) sendAmount,DATE_FORMAT(send_time_day ,'%Y-%m-%d') sendTimeDay
FROM ${tableName}
where send_time_day between #{sendPageReq.startTime} and #{sendPageReq.endTime}

    and operator like #{sendPageReq.callerAccount}


    and creator = #{sendPageReq.creator}

and deleted = '0'
GROUP BY sendTimeDay

SELECT SUM(success_num) successNum,SUM(send_num) sendAmount,send_time_hour
FROM ${tableName}
where send_time_day = #{sendPageReq.startTime}

    and operator like #{sendPageReq.callerAccount}


    and creator = #{sendPageReq.creator}

and deleted = '0'
GROUP BY send_time_hour

SELECT SUM(success_num) successNum,SUM(send_num) sendAmount
     ,sum(failed_num) failedNum,sum(unknow_num) unknowNum, send_time_hour
FROM ${tableName}
where send_time_day = #{sendPageReq.specificTime}

    and plan_id = #{sendPageReq.planId}


    and plan_id != '0'


    and plan_detail_id = #{sendPageReq.planDetailId}


    and creator = #{sendPageReq.creator}


    and short_msg_account_id = #{sendPageReq.accountId}

and deleted = '0'
GROUP BY send_time_hour

SELECT SUM(success_num) successNum,SUM(send_num) sendAmount
,sum(failed_num) failedNum,sum(unknow_num) unknowNum,DATE_FORMAT(send_time_day ,'%Y-%m-%d') sendTimeDay
FROM ${tableName}
where send_time_day between #{sendPageReq.startTime} and #{sendPageReq.endTime}

    and plan_id = #{sendPageReq.planId}


    and plan_detail_id = #{sendPageReq.planDetailId}


    and creator = #{sendPageReq.creator}


    and operator = #{sendPageReq.callerAccount}


    and short_msg_account_id = #{sendPageReq.accountId}


    and plan_id != '0'

and deleted = '0'
GROUP BY sendTimeDay

SELECT SUM(send_num) sendAmount,send_time_hour,short_msg_operator_code as operator
FROM ${tableName}

    send_time_day = #{sendPageReq.specificTime}
    and deleted = '0'
    and plan_id != 0

GROUP BY send_time_hour,operator

SELECT SUM(send_num) sendAmount,DATE_FORMAT(send_time_day ,'%Y-%m-%d') sendTimeDay,short_msg_operator_code as operator
FROM ${tableName}

    send_time_day between #{sendPageReq.startTime} and #{sendPageReq.endTime}
    and deleted = '0'
    and plan_id != 0

GROUP BY sendTimeDay,operator

SELECT SUM(send_num) sendAmount,short_msg_operator_code as operator FROM ${tableName}
deleted = '0'
GROUP BY operator

SELECT SUM(send_num) sendAmount,short_msg_operator_code as operator FROM ${tableName}
send_time_day = #{sendPageReq.specificTime} and deleted = '0'
GROUP BY operator

SELECT  GROUP_CONCAT(DISTINCT short_msg_account_id SEPARATOR ',') AS operators,send_time_hour as sendTimeHour
FROM ${tableName}
WHERE DATE_FORMAT(send_time_day ,'%Y-%m-%d') = #{sendPageReq.specificTime}

GROUP BY sendTimeHour

SELECT  GROUP_CONCAT(DISTINCT short_msg_account_id SEPARATOR ',') AS operators,DATE_FORMAT(send_time_day ,'%Y-%m-%d') as sendTimeDay
FROM ${tableName}
WHERE send_time_day between #{sendPageReq.startTime} and #{sendPageReq.endTime}

GROUP BY sendTimeDay

SELECT  SUM(send_num) sendAmount,short_msg_operator_code as operator
FROM ${tableName}
WHERE DATE_FORMAT(send_time_day,'%Y-%m-%d') <= #{yesterday}

and deleted = '0'
GROUP BY operator

SELECT  SUM(send_num) sendAmount,short_msg_operator_code as operator,DATE_FORMAT(send_time_day ,'%Y-%m-%d') as sendTimeDay
FROM ${tableName}
WHERE DATE_FORMAT(send_time_day ,'%Y-%m-%d') between #{sendPageReq.startTime} and #{sendPageReq.endTime}

and deleted = '0'
GROUP BY operator,sendTimeDay

SELECT  SUM(send_num) sendAmount,short_msg_operator_code as operator,send_time_hour as sendTimeHour
FROM ${tableName}
WHERE DATE_FORMAT(send_time_day ,'%Y-%m-%d') = #{sendPageReq.specificTime}

and deleted = '0'
GROUP BY short_msg_operator_code,sendTimeHour

SELECT  SUM(send_num) sendAmount,send_time_hour as sendTimeHour
FROM ${tableName}
WHERE DATE_FORMAT(send_time_day ,'%Y-%m-%d') = #{sendPageReq.specificTime}
and deleted = '0'
and plan_id != 0
GROUP BY sendTimeHour

SELECT  SUM(send_num) sendAmount,DATE_FORMAT(send_time_day ,'%Y-%m-%d') as sendTimeDay
FROM ${tableName}
WHERE DATE_FORMAT(send_time_day ,'%Y-%m-%d') between #{sendPageReq.startTime} and #{sendPageReq.endTime}
and deleted = '0'
and plan_id != 0
GROUP BY sendTimeDay
